mapper.select:
select NVL( count( * ), 0 ) from MESSAGE
where RECEIVE_USER_EMAIL = #{memberId} 
and MESSAGE_READ = 'N'

select * from MESSAGE
where RECEIVE_USER_EMAIL = #{memberId} 
order by MESSAGE_DATE desc

mapper.update:
update message set
MESSAGE_READ = #{messageRead}
where MESSAGE_NO = #{messageNo}
